package com.example.myapplication.auth.presentation.ui.loginimport android.content.Contextimport android.widget.Toastimport androidx.compose.material3.*import androidx.compose.runtime.*import androidx.compose.ui.graphics.Colorimport androidx.compose.ui.unit.spimport androidx.navigation.NavControllerimport com.example.myapplication.auth.data.LoginStateimport com.example.myapplication.auth.presentation.ui.theme.btnFont@Composablefun HandleLoginState(    navLogin:NavController,    navHome: NavController,    context: Context,    loginState: LoginState?) {    when (loginState) {        is LoginState.Loading -> CircularProgressIndicator()        is LoginState.Success -> {            Text(loginState.msg, color = Color.Green)            LaunchedEffect(Unit) {                Toast.makeText(context, loginState.msg, Toast.LENGTH_LONG).show()                navHome.navigate("home"){                    popUpTo("auth"){                        inclusive =true                    }                }            }        }        is LoginState.Error -> {            Text(loginState.msg, color = Color.Red)            LaunchedEffect(Unit) {                Toast.makeText(context, loginState.msg, Toast.LENGTH_LONG).show()                if (loginState.isVerified == 0) {                    navLogin.navigate("verify") {                        popUpTo("login") {                            inclusive = true                        }                    }                }            }        }        else -> {            Text("Login", fontSize = btnFont, color = Color.White)        }    }}